loadStockListFromTxt <- function(filePath = "data/stocklist.txt"){
  # Load stock list from txt file that is generated by MS Excel in unicode format
  myStock = read.table(file = filePath,header=TRUE,fileEncoding = "UTF-8",colClasses = "character")
  return(myStock)
}

