getLoc <- function(symbol){
  if(substr(symbol,1,2)=="60"){
    loc = "SH"    # shanghai
  }
  if(substr(symbol,1,3)=="000" | substr(symbol,1,3)=="300" | substr(symbol,1,3)=="002"){
    loc = "SZ"    # shenzhen
  }
  return(loc)
}